.sharebutton{padding:5px 1px 5px 1px;width:73px;float:left;z-index:250;list-style:none;}
.getsocial{width:73px;background:#EAEAF4 url(../images/sharetitle_hide.png) no-repeat bottom right;border:1px solid #D9D9ED;position:absolute;top:200px;left:0px;z-index:200;border-radius: 3px 3px 3px 3px;-moz-border-radius: 3px 3px 3px 3px;-webkit-border-top-left-radius: 3px;-webkit-border-bottom-left-radius: 3px;-webkit-border-top-right-radius: 3px;-webkit-border-bottom-right-radius: 3px;border-top-left-radius: 3px 3px;border-bottom-left-radius: 3px 3px;border-top-right-radius: 3px 3px;border-bottom-right-radius: 3px 3px}
.sharebutton.sharefooter{color:#000000;font-size:0.6em;padding:0px 2px 0 2px;margin:0px 1px 0px 2px;width:73px;float:left;z-index:250;list-style:none;}
.sharebutton.sharefooter a{text-decoration:none;color:#000000;}
.FBConnectButton_Small{background-position:-5px -232px !important;border-left:1px solid #1A356E}
